import React, {Component} from 'react';

class AddQuestion extends Component {

    putDataToDB = (title,description) => {
        console.log('its me ' + this.state.questions.map(questions => questions.id));
        let currentIds = this.state.questions.map(questions => questions.id);
        let idToBeAdded = 0;
        while (currentIds.includes(idToBeAdded)) {
            ++idToBeAdded;
        }};
    render() {
        return (
            <div style={{ padding: "10px" }}>
                <input
                    type="text"
                    onChange={e => this.setState({ title: e.target.value })}
                    placeholder="How do I hack NASA"
                    style={{ width: "200px" }}
                />
                <input
                    type="text"
                    onChange={e => this.setState({ description: e.target.value })}
                    placeholder="I just learn html and want to hack NASA, can you help me?"
                    style={{ width: "200px" }}
                />
                <button onClick={() => this.putDataToDB(this.state.title, this.state.description )}>
                    ADD
                </button>
            </div>
        );
    }
}
export default AddQuestion;
