import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";

import RecipeList from "./RecipeList";
import Recipe from "./Recipe";
import NotFound from "./NotFound";
import Authors from "./Authors";

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: []
        }
    }

    componentDidMount() {
        this.getData();
    }

    getData(){
        console.log("Getting data");
        fetch(`${this.API_URL}/recipe`)
            .then(response =>{
                console.log(response)
                response.json()})
            .then(json =>{
                this.setState({data: json})
            })
    }
    // TODO: Remove these two methods
    getRecipeFromId(id) {
        return this.state.data.find((elm) => elm.id === Number(id));
    }

    filterByIngredient(ingredient) {
        return this.state.data.filter((elm) => elm.ingredients.includes(ingredient))
    }

    filterByAuthor(author){
        return this.state.data.filter((elm)=> elm.author.includes(author))
    }

    render() {
        return (
            <Router>
                <div className="container">
                    <h1>Cooking Recipes!</h1>

                    <Switch>
                        <Route exact path={'/'}
                               render={(props) =>
                                   <RecipeList {...props}
                                         recipes={this.state.data}
                                         header={'All recipes'}/>}
                        />

                        <Route exact path={'/recipe/:id'}
                               render={(props) => <Recipe {...props}
                               recipe={this.getRecipeFromId(props.match.params.id)}/>}
                        />

                        <Route exact path={'/recipe/with/:ingredient'}
                               render={(props) =>
                                   <RecipeList {...props}
                                        recipes={this.filterByIngredient(props.match.params.ingredients)}
                                        header={`Recipes with ${props.match.params.ingredients}`}/>}
                        />
                        <Route exact path ={'/recipe/:author'}
                                render={(props)=>
                                <Authors{...props}
                                authors={this.filterByAuthor(props.match.params.author)}
                                header={`Recipes from ${props.match.param.author}`}/>
                                }
                        />

                        <Route component={NotFound} />
                    </Switch>

                </div>
            </Router>
        );
    }
}

export default App;
