import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";

import RecipeList from "./RecipeList";
import Recipe from "./Recipe";
import NotFound from "./NotFound";

class App extends Component {

    constructor(props) {
        super(props);

        // TODO: Move this data to the server
        this.state = {
            recipes: [
                {
                    id: 0,
                    title: "Pizza",
                    description: "Pizza is nice",
                    ingredients: ['cheese', 'tomato', 'onion'],
                    prep_time: 20,
                    total_time: 30
                },
                {
                    id: 1,
                    title: "Vegetable Quiche",
                    description: "Nice with shredded zucchini",
                    ingredients: ['cheese', 'zucchini', 'onion'],
                    prep_time: 30,
                    total_time: 90
                },
                {
                    id: 2,
                    title: "Baked Potato with Fried Eggs",
                    description: "Served with bakes beans",
                    ingredients: ['potato', 'beans', 'egg'],
                    prep_time: 10,
                    total_time: 70
                },
            ]
        }
    }

    // TODO: Remove these two methods
    getRecipeFromId(id) {
        return this.state.recipes.find((elm) => elm.id === Number(id));
    }

    filterByIngredient(ingredient) {
        return this.state.recipes.filter((elm) => elm.ingredients.includes(ingredient))
    }

    render() {
        return (
            <Router>
                <div className="container">
                    <h1>Cooking Recipes!</h1>

                    <Switch>
                        <Route exact path={'/'}
                               render={(props) =>
                                   <RecipeList {...props}
                                         recipes={this.state.recipes}
                                         header={'All recipes'}/>}
                        />

                        <Route exact path={'/recipe/:id'}
                               render={(props) => <Recipe {...props}
                               recipe={this.getRecipeFromId(props.match.params.id)}/>}
                        />

                        <Route exact path={'/recipe/with/:ingredient'}
                               render={(props) =>
                                   <RecipeList {...props}
                                        recipes={this.filterByIngredient(props.match.params.ingredient)}
                                        header={`Recipes with ${props.match.params.ingredient}`}/>}
                        />

                        <Route component={NotFound} />
                    </Switch>

                </div>
            </Router>
        );
    }
}

export default App;
