import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom';
import Switch from "react-switch";
import './index.css';
import Question from "./Question";
import QuestionList from "./QuestionList";
import AddQuestion from "./AddQuestion";
import NotFound from "./NotFound";
import axios from 'axios';
var testtest;

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            questions: this.getData(),
        };
        this.state = { checked: false };
        this.handleChange = this.handleChange.bind(this);
        this.API_URL=window.location.href;
    }
    handleChange() {
        console.log("test");
    }
    componentDidMount() {
        this.getData();
    }
    getData(){
        console.log("Getting data");
        // fetch(`http://localhost:8080/questions2`)
        //     .then(response =>{
        //         console.log(response);
        //
        //     })
        //     .then(json =>{
        //         this.setState({questions: json});
        //     });
        axios.get('http://localhost:8080/questions2')
            .then(response =>
                testtest = response.data
            )
        return testtest
    }

    addQuestion(text) {
        let newQuestion = {
            id: Math.floor(Math.random() * Math.floor(1000000)),
            task: text,
            done: false
        };
        this.setState({
            questions: [...this.state.questions, newQuestion]
        });
    }
    getQuestionFromId(id) {
        return this.state.questions.find((elm) => elm.id === Number(id));
    }

    filterByTopic(topic) {
        return this.state.questions.filter((elm) => elm.topic.includes(topic))
    }

    render() {
        return (
            <Router>
                <div className="container">
                    <h1>StackOverflow</h1>

                    <Switch onChange={this.handleChange} checked={this.state.checked} >
                        <Route exact path={'/'}
                               render={(props) =>
                                   <QuestionList {...props}
                                                 questions={this.state.questions}
                                                 header={'Questions Asked'}/>
                               }
                        />

                        <Route exact path={'/questions/:id'}
                               render={(props) => <Question {...props}
                                                            questions={this.getQuestionFromId(props.match.params.id)}/>}
                        />

                        <Route exact path={'/questions/with/:topic'}
                               render={(props) =>
                                   <QuestionList {...props}
                                                 questions={this.filterByTopic(props.match.params.topic)}
                                                 header={`Questions that consist ${props.match.params.topic}`}/>}
                        />

                        <Route component={NotFound} />
                    </Switch>

                </div>
            </Router>
        );
    }
}

export default App;
